import styled from 'styled-components';

interface DragPreviewProps {
  isDragging: boolean;
  type: string;
}

interface DropZonePreviewProps {
  isOver: boolean;
  isValid: boolean;
}

const getPreviewStyles = (type: string) => {
  switch (type) {
    case 'week':
      return {
        padding: '16px',
        width: '100%',
        minHeight: '100px',
      };
    case 'workout':
      return {
        padding: '12px',
        width: '300px',
        minHeight: '80px',
      };
    case 'exercise':
      return {
        padding: '8px',
        width: '100%',
        minHeight: '40px',
      };
    default:
      return {};
  }
};

export const DragPreview = styled.div<DragPreviewProps>`
  opacity: ${({ isDragging }) => (isDragging ? 0.5 : 1)};
  transform: scale(${({ isDragging }) => (isDragging ? 0.95 : 1)});
  transition: all ${({ theme }) => theme.transitions.fast};
  ${({ type }) => getPreviewStyles(type)}
`;

export const DropZonePreview = styled.div<DropZonePreviewProps>`
  background: ${({ theme, isOver, isValid }) =>
    isOver
      ? isValid
        ? theme.colors.success + '20'
        : theme.colors.error + '20'
      : 'transparent'};
  border-radius: ${({ theme }) => theme.borderRadius.md};
  transition: background-color ${({ theme }) => theme.transitions.fast};
  pointer-events: none;

  & > * {
    pointer-events: auto;
  }
`;
