import React from 'react';
import styled from 'styled-components';
import { Routine } from '../types/routine';
import { Section, Flex } from '../styles/components/Layout.styles';
import { WeekGrid } from '../styles/components/Grid.styles';
import WeekComponent from './Week';
import EditableTitle from './EditableTitle';
import SaveButton from './SaveButton';

const RoutineContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 2rem;
  width: 100%;
  overflow-x: auto;
  padding-bottom: 2rem;

  /* Hide scrollbar for Chrome, Safari and Opera */
  &::-webkit-scrollbar {
    display: none;
  }

  /* Hide scrollbar for IE, Edge and Firefox */
  -ms-overflow-style: none;
  scrollbar-width: none;

  @media (min-width: 768px) {
    overflow-x: visible;
  }
`;

const WeekList = styled.div`
  display: flex;
  flex-direction: column;
  gap: 2rem;
  min-width: min-content;
  padding: 0.5rem;

  @media (min-width: 768px) {
    min-width: 100%;
    padding: 0;
  }
`;

const Header = styled.header`
  margin-bottom: ${({ theme }) => theme.spacing.lg};
`;

const Title = styled.h1`
  color: ${({ theme }) => theme.colors.text};
  font-size: ${({ theme }) => theme.typography.fontSizes.xl};
  font-weight: ${({ theme }) => theme.typography.fontWeights.bold};
  margin: 0;
`;

const SaveStatus = styled.span`
  color: ${({ theme }) => theme.colors.secondary};
  font-size: ${({ theme }) => theme.typography.fontSizes.sm};
`;

const ErrorMessage = styled.span`
  color: ${({ theme }) => theme.colors.error};
  font-size: ${({ theme }) => theme.typography.fontSizes.sm};
`;

interface RoutineProps {
  routine: Routine;
  onRoutineChange: (routine: Routine) => void;
  isDirty: boolean;
  isSaving: boolean;
  lastSaved: Date | null;
  error: string | null;
  onSave: () => void;
  onAddWorkout: (weekId: string) => void;
  onAddExercise: (weekId: string, workoutId: string) => void;
}

const RoutineComponent: React.FC<RoutineProps> = ({
  routine,
  onRoutineChange,
  isDirty,
  isSaving,
  lastSaved,
  error,
  onSave,
  onAddWorkout,
  onAddExercise
}) => {
  const handleTitleChange = (newTitle: string) => {
    onRoutineChange({
      ...routine,
      name: newTitle
    });
  };

  const formatLastSaved = (date: Date) => {
    return new Intl.DateTimeFormat('en-US', {
      hour: 'numeric',
      minute: 'numeric',
      second: 'numeric'
    }).format(date);
  };

  return (
    <Section>
      <Header>
        <Flex justify="space-between" align="center">
          <EditableTitle
            value={routine.name}
            onChange={handleTitleChange}
          />
          <SaveButton
            isDirty={isDirty}
            isSaving={isSaving}
            lastSaved={lastSaved}
            error={error}
            onSave={onSave}
          />
        </Flex>
      </Header>
      <WeekGrid>
        {routine.weeks.map((week, index) => (
          <WeekComponent
            key={week.id}
            week={week}
            index={index}
            onAddWorkout={() => onAddWorkout(week.id)}
            onAddExercise={onAddExercise}
          />
        ))}
      </WeekGrid>
    </Section>
  );
};

export default RoutineComponent;
