import React, { useState } from 'react';
import styled from 'styled-components';
import { Draggable, Droppable } from 'react-beautiful-dnd';
import type { Week } from '../types/routine';
import { Card, Flex } from '../styles/components/Layout.styles';
import { WorkoutGrid, DragDropGrid, WeekContainer } from '../styles/components/Grid.styles';
import WorkoutComponent from './Workout';
import { DragPreview, DropZonePreview } from './DragPreview';

const WeekCard = styled(Card)`
  background: ${({ theme }) => theme.colors.background};
  margin-bottom: ${({ theme }) => theme.spacing.lg};
  border: 1px solid ${({ theme }) => theme.colors.border};
  transition: all ${({ theme }) => theme.transitions.fast};

  &:hover {
    border-color: ${({ theme }) => theme.colors.primary};
    box-shadow: ${({ theme }) => theme.shadows.md};
  }
`;

const WeekHeader = styled.div`
  margin-bottom: ${({ theme }) => theme.spacing.md};
  padding: ${({ theme }) => theme.spacing.md};
  border-bottom: 1px solid ${({ theme }) => theme.colors.border};
  background: ${({ theme }) => theme.colors.surface};
  border-top-left-radius: ${({ theme }) => theme.borderRadius.md};
  border-top-right-radius: ${({ theme }) => theme.borderRadius.md};
  cursor: pointer;
  user-select: none;

  &:hover {
    background: ${({ theme }) => theme.colors.hover};
  }
`;

const WeekTitle = styled.h2`
  color: ${({ theme }) => theme.colors.text};
  font-size: ${({ theme }) => theme.typography.fontSizes.lg};
  font-weight: ${({ theme }) => theme.typography.fontWeights.semibold};
  margin: 0;
`;

const DragHandle = styled.div`
  width: 24px;
  height: 24px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: grab;
  color: ${({ theme }) => theme.colors.secondary};
  opacity: 0.5;
  transition: opacity ${({ theme }) => theme.transitions.fast};

  &:hover {
    opacity: 1;
  }

  &:before {
    content: "⋮⋮";
    font-size: ${({ theme }) => theme.typography.fontSizes.lg};
    line-height: 1;
  }
`;

const WeekContent = styled.div<{ isCollapsed: boolean }>`
  display: ${({ isCollapsed }) => isCollapsed ? 'none' : 'block'};
  padding: ${({ theme }) => theme.spacing.md};
`;

const ScrollIndicator = styled.div<{ show: boolean; direction: 'left' | 'right' }>`
  position: absolute;
  top: 50%;
  ${({ direction }) => direction === 'left' ? 'left: 0;' : 'right: 0;'}
  transform: translateY(-50%);
  width: 32px;
  height: 32px;
  background: ${({ theme }) => theme.colors.background};
  border-radius: ${({ theme }) => theme.borderRadius.circle};
  box-shadow: ${({ theme }) => theme.shadows.md};
  display: ${({ show }) => show ? 'flex' : 'none'};
  align-items: center;
  justify-content: center;
  color: ${({ theme }) => theme.colors.primary};
  cursor: pointer;
  z-index: ${({ theme }) => theme.zIndices.sticky};
  opacity: 0.8;
  transition: opacity ${({ theme }) => theme.transitions.fast};

  &:hover {
    opacity: 1;
  }

  &::before {
    content: "${({ direction }) => direction === 'left' ? '←' : '→'}";
    font-size: ${({ theme }) => theme.typography.fontSizes.xl};
  }

  @media (max-width: ${({ theme }) => theme.breakpoints.mobile}) {
    width: 24px;
    height: 24px;
  }
`;

const WorkoutListContainer = styled.div`
  position: relative;
  width: 100%;
`;

const WorkoutList = styled.div`
  display: flex;
  gap: ${({ theme }) => theme.spacing.md};
  overflow-x: auto;
  padding: ${({ theme }) => theme.spacing.sm};
  scroll-snap-type: x mandatory;
  -webkit-overflow-scrolling: touch;
  min-height: 200px;
  align-items: flex-start;
  position: relative;
  scroll-behavior: smooth;

  &::-webkit-scrollbar {
    height: 6px;
  }

  &::-webkit-scrollbar-track {
    background: ${({ theme }) => theme.colors.surface};
    border-radius: ${({ theme }) => theme.borderRadius.circle};
  }

  &::-webkit-scrollbar-thumb {
    background: ${({ theme }) => theme.colors.primary};
    border-radius: ${({ theme }) => theme.borderRadius.circle};
  }
`;

const CollapseIndicator = styled.span<{ isCollapsed: boolean }>`
  margin-left: ${({ theme }) => theme.spacing.sm};
  transition: transform ${({ theme }) => theme.transitions.fast};
  transform: rotate(${({ isCollapsed }) => isCollapsed ? '180deg' : '0deg'});
  display: inline-block;

  &::before {
    content: "▼";
    font-size: ${({ theme }) => theme.typography.fontSizes.sm};
  }
`;

const AddButton = styled.button`
  min-width: 300px;
  height: 200px;
  padding: ${({ theme }) => theme.spacing.lg};
  background: ${({ theme }) => theme.colors.surface};
  border: 2px dashed ${({ theme }) => theme.colors.border};
  border-radius: ${({ theme }) => theme.borderRadius.md};
  color: ${({ theme }) => theme.colors.primary};
  cursor: pointer;
  transition: all ${({ theme }) => theme.transitions.fast};
  font-size: ${({ theme }) => theme.typography.fontSizes.md};
  font-weight: ${({ theme }) => theme.typography.fontWeights.medium};

  &:hover {
    background: ${({ theme }) => theme.colors.hover};
    border-color: ${({ theme }) => theme.colors.primary};
  }
`;

interface WeekProps {
  week: Week;
  index: number;
  onAddWorkout: () => void;
  onAddExercise: (workoutId: string) => void;
}

const WeekComponent: React.FC<WeekProps> = ({
  week,
  index,
  onAddWorkout,
  onAddExercise
}) => {
  const [isCollapsed, setIsCollapsed] = useState(false);
  const [showLeftScroll, setShowLeftScroll] = useState(false);
  const [showRightScroll, setShowRightScroll] = useState(true);
  const workoutListRef = React.useRef<HTMLDivElement>(null);

  const handleScroll = () => {
    if (!workoutListRef.current) return;

    const { scrollLeft, scrollWidth, clientWidth } = workoutListRef.current;
    setShowLeftScroll(scrollLeft > 0);
    setShowRightScroll(scrollLeft < scrollWidth - clientWidth - 10);
  };

  const handleScrollClick = (direction: 'left' | 'right') => {
    if (!workoutListRef.current) return;

    const scrollAmount = direction === 'left' ? -300 : 300;
    workoutListRef.current.scrollBy({ left: scrollAmount, behavior: 'smooth' });
  };

  React.useEffect(() => {
    const listElement = workoutListRef.current;
    if (listElement) {
      listElement.addEventListener('scroll', handleScroll);
      handleScroll();
    }

    return () => {
      if (listElement) {
        listElement.removeEventListener('scroll', handleScroll);
      }
    };
  }, []);

  return (
    <Draggable draggableId={week.id} index={index}>
      {(provided, snapshot) => (
        <WeekContainer
          ref={provided.innerRef}
          {...provided.draggableProps}
        >
          <WeekHeader 
            {...provided.dragHandleProps}
            onClick={() => setIsCollapsed(!isCollapsed)}
          >
            <Flex justify="space-between" align="center">
              <Flex align="center">
                <WeekTitle>{week.name}</WeekTitle>
                <CollapseIndicator isCollapsed={isCollapsed} />
              </Flex>
            </Flex>
          </WeekHeader>

          <WeekContent isCollapsed={isCollapsed}>
            <Droppable droppableId={week.id} type="WORKOUT" direction="horizontal">
              {(provided, snapshot) => (
                <WorkoutListContainer>
                  <WorkoutList
                    ref={(el) => {
                      provided.innerRef(el);
                      // @ts-ignore - we know this is an HTMLDivElement
                      workoutListRef.current = el;
                    }}
                    {...provided.droppableProps}
                  >
                    {week.workouts.map((workout, index) => (
                      <WorkoutComponent
                        key={workout.id}
                        workout={workout}
                        index={index}
                        onAddExercise={() => onAddExercise(workout.id)}
                      />
                    ))}
                    {provided.placeholder}
                    <AddButton onClick={onAddWorkout}>
                      + Add Workout
                    </AddButton>
                  </WorkoutList>
                </WorkoutListContainer>
              )}
            </Droppable>
          </WeekContent>
        </WeekContainer>
      )}
    </Draggable>
  );
};

export default WeekComponent;
