import React from 'react';
import styled from 'styled-components';
import { Droppable } from 'react-beautiful-dnd';
import { Routine as RoutineType } from '../types/routine';
import Week from './Week';
import { Section, Flex } from '../styles/components/Layout.styles';
import { WeekGrid } from '../styles/components/Grid.styles';

const RoutineContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 2rem;
  width: 100%;
  overflow-x: auto;
  padding-bottom: 2rem;

  /* Hide scrollbar for Chrome, Safari and Opera */
  &::-webkit-scrollbar {
    display: none;
  }

  /* Hide scrollbar for IE, Edge and Firefox */
  -ms-overflow-style: none;
  scrollbar-width: none;

  @media (min-width: 768px) {
    overflow-x: visible;
  }
`;

const WeekList = styled.div`
  display: flex;
  flex-direction: column;
  gap: 2rem;
  min-width: min-content;
  padding: 0.5rem;

  @media (min-width: 768px) {
    min-width: 100%;
    padding: 0;
  }
`;

const Header = styled.header`
  margin-bottom: ${({ theme }) => theme.spacing.lg};
`;

const Title = styled.h1`
  color: ${({ theme }) => theme.colors.text};
  font-size: ${({ theme }) => theme.typography.fontSizes.xl};
  font-weight: ${({ theme }) => theme.typography.fontWeights.bold};
  margin: 0;
`;

const SaveButton = styled.button<{ isDirty: boolean }>`
  background: ${({ theme, isDirty }) =>
    isDirty ? theme.colors.primary : theme.colors.disabled};
  color: ${({ theme }) => theme.colors.background};
  border: none;
  border-radius: ${({ theme }) => theme.borderRadius.md};
  padding: ${({ theme }) => `${theme.spacing.sm} ${theme.spacing.lg}`};
  font-weight: ${({ theme }) => theme.typography.fontWeights.medium};
  cursor: ${({ isDirty }) => (isDirty ? 'pointer' : 'default')};
  transition: background-color ${({ theme }) => theme.transitions.fast};

  &:hover {
    background: ${({ theme, isDirty }) =>
      isDirty ? theme.colors.success : theme.colors.disabled};
  }
`;

const SaveStatus = styled.span`
  color: ${({ theme }) => theme.colors.secondary};
  font-size: ${({ theme }) => theme.typography.fontSizes.sm};
`;

const ErrorMessage = styled.span`
  color: ${({ theme }) => theme.colors.error};
  font-size: ${({ theme }) => theme.typography.fontSizes.sm};
`;

interface RoutineProps {
  routine: RoutineType;
  onAddWorkout: (weekId: string) => void;
  onAddExercise: (weekId: string, workoutId: string) => void;
  onRoutineChange: (routine: RoutineType) => void;
  isDirty: boolean;
  isSaving: boolean;
  lastSaved: Date | null;
  error: string | null;
  onSave: () => void;
}

const Routine: React.FC<RoutineProps> = ({
  routine,
  onAddWorkout,
  onAddExercise,
  onRoutineChange,
  isDirty,
  isSaving,
  lastSaved,
  error,
  onSave
}) => {
  const formatLastSaved = (date: Date) => {
    return new Intl.DateTimeFormat('en-US', {
      hour: 'numeric',
      minute: 'numeric',
      second: 'numeric'
    }).format(date);
  };

  return (
    <Section>
      <Header>
        <Flex justify="space-between" align="center">
          <Title>{routine.name}</Title>
          <Flex align="center" gap="md">
            {error ? (
              <ErrorMessage>{error}</ErrorMessage>
            ) : lastSaved ? (
              <SaveStatus>
                Last saved at {formatLastSaved(lastSaved)}
              </SaveStatus>
            ) : null}
            <SaveButton
              onClick={onSave}
              disabled={!isDirty || isSaving}
              isDirty={isDirty}
            >
              {isSaving ? 'Saving...' : 'Save'}
            </SaveButton>
          </Flex>
        </Flex>
      </Header>
      <WeekGrid>
        <Droppable droppableId="weeks" type="WEEK">
          {(provided) => (
            <WeekList
              ref={provided.innerRef}
              {...provided.droppableProps}
            >
              {routine.weeks.map((week, index) => (
                <Week
                  key={week.id}
                  week={week}
                  index={index}
                  onAddWorkout={() => onAddWorkout(week.id)}
                  onAddExercise={onAddExercise}
                />
              ))}
              {provided.placeholder}
            </WeekList>
          )}
        </Droppable>
      </WeekGrid>
    </Section>
  );
};

export default Routine;
