import React from 'react';
import styled, { keyframes } from 'styled-components';
import { Card } from '../styles/components/Layout.styles';

const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(-4px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const PreviewCard = styled(Card)<{ isDragging: boolean }>`
  opacity: ${({ isDragging }) => (isDragging ? 0.9 : 1)};
  transform: scale(${({ isDragging }) => (isDragging ? 1.02 : 1)});
  box-shadow: ${({ theme, isDragging }) =>
    isDragging ? theme.shadows.lg : theme.shadows.sm};
  border: 2px solid ${({ theme }) => theme.colors.primary};
  background: ${({ theme }) => theme.colors.background};
  transition: all ${({ theme }) => theme.transitions.fast};
  animation: ${fadeIn} 0.2s ease-out;
  pointer-events: none;
  user-select: none;
`;

const DropZone = styled.div<{ isOver: boolean; isValid: boolean }>`
  border: 2px dashed ${({ theme, isValid }) =>
    isValid ? theme.colors.primary : theme.colors.error};
  background: ${({ theme, isOver, isValid }) =>
    isOver
      ? `${isValid ? theme.colors.primary : theme.colors.error}1A`
      : 'transparent'};
  border-radius: ${({ theme }) => theme.borderRadius.md};
  min-height: 60px;
  transition: all ${({ theme }) => theme.transitions.fast};
  opacity: ${({ isOver }) => (isOver ? 0.7 : 0.3)};
`;

interface DragPreviewProps {
  children: React.ReactNode;
  isDragging: boolean;
  type: 'week' | 'workout' | 'exercise';
}

const getPreviewStyles = (type: string) => {
  switch (type) {
    case 'week':
      return {
        padding: '16px',
        width: '100%',
        minHeight: '100px',
      };
    case 'workout':
      return {
        padding: '12px',
        width: '300px',
        minHeight: '80px',
      };
    case 'exercise':
      return {
        padding: '8px',
        width: '100%',
        minHeight: '40px',
      };
    default:
      return {};
  }
};

export const DragPreview: React.FC<DragPreviewProps> = ({
  children,
  isDragging,
  type,
}) => {
  const styles = getPreviewStyles(type);

  return (
    <PreviewCard isDragging={isDragging} style={styles}>
      {children}
    </PreviewCard>
  );
};

interface DropZonePreviewProps {
  isOver: boolean;
  isValid: boolean;
  children?: React.ReactNode;
}

export const DropZonePreview: React.FC<DropZonePreviewProps> = ({
  isOver,
  isValid,
  children,
}) => {
  return (
    <DropZone isOver={isOver} isValid={isValid}>
      {children}
    </DropZone>
  );
};
